<?php$tags = array();foreach( $list->getTags()->toArray() as $tag )	$tags[] = $tag->serializeProperties();$items = array();foreach( $list->getItems()->toArray() as $item )	$items[] = $item->serializeProperties();$listProps = $list->serializeProperties();$listProps['tags'] = $tags;$listProps['items'] = $items;echo json_encode(	array(		'status'	=>	'ok',		'list'		=>	$listProps,	));