<?php echo WidgetBuilder::getFormTag( $question ); ?>	<div id="question" class="question">		<?php echo $question->QuestionText; ?>		<div id="question-groups" class="question-groups">			<?php foreach( $question->getQuestionGroups() as $questionGroup ): ?>				<div class="question-group">					<?php echo $questionGroup->Label; ?>					<?php foreach( $questionGroup->getAnswerGroups() as $answerGroup ): ?>						<?php echo $answerGroup->Label; ?>						<?php/* echo WidgetBuilder::getWidget( $answerGroup ); */ ?>						<ul>							<?php foreach( WidgetBuilder::randomizeAnswers( $answerGroup->getAnswers() ) as $answer ): ?>							<li><?php echo WidgetBuilder::getWidget( $answer ); ?></li>							<?php endforeach; ?>						</ul>					<?php endforeach; ?>				</div>			<?php endforeach; ?>		</div>		<?php echo WidgetBuilder::getSubmitButton( 'Next' ); ?>	</div></form>